// ComplexSystem class representing a complex system
public class ComplexSystem {
    // Static variable
    private static int systemCount = 0;

    // Instance variables
    private String systemName;
    private Component[] components;
    private SubSystem subSystem;

    // Constructor
    public ComplexSystem(String systemName, Component[] components, SubSystem subSystem) {
        this.systemName = systemName;
        this.components = components;
        this.subSystem = subSystem;
        systemCount++;
    }

    // Getter methods
    public String getSystemName() {
        return systemName;
    }

    public Component[] getComponents() {
        return components;
    }

    public SubSystem getSubSystem() {
        return subSystem;
    }

    public static int getSystemCount() {
        return systemCount;
    }

    // Method to display system details
    @Override
    public String toString() {
        return "ComplexSystem{" +
                "systemName='" + systemName + '\'' +
                ", components=" + Arrays.toString(components) +
                ", subSystem=" + subSystem +
                '}';
    }
}

// Component class representing a component in the complex system
public class Component {
    private String componentName;

    // Constructor
    public Component(String componentName) {
        this.componentName = componentName;
    }

    // Getter method
    public String getComponentName() {
        return componentName;
    }

    // Method to display component details
    @Override
    public String toString() {
        return "Component{" +
                "componentName='" + componentName + '\'' +
                '}';
    }
}

// SubSystem class representing a subsystem in the complex system
public class SubSystem {
    private String subSystemName;

    // Constructor
    public SubSystem(String subSystemName) {
        this.subSystemName = subSystemName;
    }

    // Getter method
    public String getSubSystemName() {
        return subSystemName;
    }

    // Method to display subsystem details
    @Override
    public String toString() {
        return "SubSystem{" +
                "subSystemName='" + subSystemName + '\'' +
                '}';
    }
}

// Main class for testing
public class Main {
    public static void main(String[] args) {
        // Creating components
        Component component1 = new Component("Component 1");
        Component component2 = new Component("Component 2");
        Component[] components = {component1, component2};

        // Creating subsystem
        SubSystem subSystem = new SubSystem("SubSystem 1");

        // Creating complex system
        ComplexSystem system = new ComplexSystem("Complex System", components, subSystem);

        // Displaying system details
        System.out.println("System Details:");
        System.out.println(system);

        // Displaying total system count
        System.out.println("Total System Count: " + ComplexSystem.getSystemCount());
    }
}

