import java.util.ArrayList;
import java.util.List;

// Song class to represent individual songs
class Song {
    private String title;
    private String artist;
    private double duration; // in minutes

    public Song(String title, String artist, double duration) {
        this.title = title;
        this.artist = artist;
        this.duration = duration;
    }

    // Getter methods
    public String getTitle() {
        return title;
    }

    public String getArtist() {
        return artist;
    }

    public double getDuration() {
        return duration;
    }

    // Method to display song information
    public void displayInfo() {
        System.out.println("Title: " + title);
        System.out.println("Artist: " + artist);
        System.out.println("Duration: " + duration + " minutes");
    }
}

// Playlist class to manage a collection of songs
class Playlist {
    private String name;
    private List<Song> songs;

    public Playlist(String name) {
        this.name = name;
        this.songs = new ArrayList<>();
    }

    // Method to add a song to the playlist
    public void addSong(Song song) {
        songs.add(song);
    }

    // Method to display all songs in the playlist
    public void displayPlaylist() {
        System.out.println("Playlist: " + name);
        for (Song song : songs) {
            song.displayInfo();
            System.out.println();
        }
    }
}

// Player class to manage the music player functionality
class Player {
    private List<Playlist> playlists;
    private List<Song> library;

    public Player() {
        this.playlists = new ArrayList<>();
        this.library = new ArrayList<>();
    }

    // Method to create a new playlist
    public void createPlaylist(String name) {
        Playlist playlist = new Playlist(name);
        playlists.add(playlist);
    }

    // Method to add a song to the library
    public void addToLibrary(Song song) {
        library.add(song);
    }

    // Method to add a song from the library to a playlist
    public void addSongToPlaylist(Song song, String playlistName) {
        for (Playlist playlist : playlists) {
            if (playlistName.equals(playlist.getName())) {
                playlist.addSong(song);
                return;
            }
        }
        System.out.println("Playlist not found.");
    }

    // Method to display all playlists
    public void displayPlaylists() {
        for (Playlist playlist : playlists) {
            playlist.displayPlaylist();
        }
    }
}

public class Main {
    public static void main(String[] args) {
        Player player = new Player();

        // Adding songs to the library
        Song song1 = new Song("Shape of You", "Ed Sheeran", 4.23);
        Song song2 = new Song("Believer", "Imagine Dragons", 3.24);
        Song song3 = new Song("Memories", "Maroon 5", 3.09);
        player.addToLibrary(song1);
        player.addToLibrary(song2);
        player.addToLibrary(song3);

        // Creating playlists
        player.createPlaylist("Favorites");
        player.createPlaylist("Party Mix");

        // Adding songs to playlists
        player.addSongToPlaylist(song1, "Favorites");
        player.addSongToPlaylist(song2, "Favorites");
        player.addSongToPlaylist(song3, "Favorites");
        player.addSongToPlaylist(song2, "Party Mix");

        // Displaying playlists
        player.displayPlaylists();
    }
}

