// Shape.java
public abstract class Shape {
    // Abstract method to calculate area
    public abstract double calculateArea();

    // Abstract method to calculate perimeter
    public abstract double calculatePerimeter();
}

// Circle.java
public class Circle extends Shape {
    private double radius;

    // Constructor
    public Circle(double radius) {
        this.radius = radius;
    }

    // Override method to calculate area for Circle
    @Override
    public double calculateArea() {
        return Math.PI * radius * radius;
    }

    // Override method to calculate perimeter for Circle
    @Override
    public double calculatePerimeter() {
        return 2 * Math.PI * radius;
    }
}

// Rectangle.java
public class Rectangle extends Shape {
    private double width;
    private double height;

    // Constructor
    public Rectangle(double width, double height) {
        this.width = width;
        this.height = height;
    }

    // Override method to calculate area for Rectangle
    @Override
    public double calculateArea() {
        return width * height;
    }

    // Override method to calculate perimeter for Rectangle
    @Override
    public double calculatePerimeter() {
        return 2 * (width + height);
    }
}

// Main.java
public class Main {
    public static void main(String[] args) {
        // Creating instances of Circle and Rectangle
        Shape circle = new Circle(5);
        Shape rectangle = new Rectangle(4, 6);

        // Calculating and printing area and perimeter of Circle
        System.out.println("Circle:");
        System.out.println("Area: " + circle.calculateArea());
        System.out.println("Perimeter: " + circle.calculatePerimeter());

        // Calculating and printing area and perimeter of Rectangle
