class Library {
  private String name; // Encapsulated attribute
  String address;
  Map<String, Shelf> shelves;

  public Library(String name, String address) {
    this.name = name;
    this.address = address;
    this.shelves = new HashMap<>();
  }

  public String getName() {
    return name; // Accessor method for encapsulated attribute
  }

  public void add_shelf(Shelf shelf) {
    shelves.put(shelf.name, shelf);
  }

  public void add_item(String shelf_name, LibraryItem item) {
    if (shelves.containsKey(shelf_name)) {
      shelves.get(shelf_name).add_item(item);
    } else {
      System.out.println(f"Shelf {shelf_name} does not exist");
    }
  }

  public void borrow_item(String title) {
    for (Shelf shelf : shelves.values()) {
      for (LibraryItem item : shelf.get_items()) {
        if (item.getTitle().equals(title)) {
          item.borrow();
          return;
        }
      }
    }
    System.out.println(f"Item with title {title} not found");
  }
}

