// Interface for different account types
interface Account {
  public void deposit(double amount);
  public void withdraw(double amount) throws InsufficientFundsException;
}

// Savings Account class with specific withdraw logic
class SavingsAccount implements Account {
  private double balance;

  public SavingsAccount(double balance) {
    this.balance = balance;
  }

  @Override
  public void deposit(double amount) {
    balance += amount;
  }

  @Override
  public void withdraw(double amount) throws InsufficientFundsException {
    if (amount > balance) {
      throw new InsufficientFundsException("Insufficient funds in savings account");
    }
    balance -= amount;
  }
}

// Current Account class with overdraft protection and potentially different withdraw logic
class CurrentAccount implements Account {
  private double balance;
  private double overdraftLimit;

  public CurrentAccount(double balance, double overdraftLimit) {
    this.balance = balance;
    this.overdraftLimit = overdraftLimit;
  }

  @Override
  public void deposit(double amount) {
    balance += amount;
  }

  @Override
  public void withdraw(double amount) throws InsufficientFundsException {
    if (amount > balance + overdraftLimit) {
      throw new InsufficientFundsException("Insufficient funds in current account");
    }
    balance -= amount;
  }
}

// Custom exception class for insufficient funds
class InsufficientFundsException extends Exception {
  public InsufficientFundsException(String message) {
    super(message);
  }
}

