// Base Account class
class Account {
    protected String accountNumber;
    protected double balance;

    public Account(String accountNumber, double balance) {
        this.accountNumber = accountNumber;
        this.balance = balance;
    }

    public void deposit(double amount) {
        balance += amount;
    }

    public void withdraw(double amount) {
        if (amount <= balance) {
            balance -= amount;
        } else {
            System.out.println("Insufficient funds!");
        }
    }

    public void displayBalance() {
        System.out.println("Account Balance: $" + balance);
    }
}

// Savings Account subclass
class SavingsAccount extends Account {
    private double interestRate;

    public SavingsAccount(String accountNumber, double balance, double interestRate) {
        super(accountNumber, balance);
        this.interestRate = interestRate;
    }

    public void addInterest() {
        balance += balance * interestRate;
    }
}

// Checking Account subclass
class CheckingAccount extends Account {
    private double overdraftLimit;

    public CheckingAccount(String accountNumber, double balance, double overdraftLimit) {
        super(accountNumber, balance);
        this.overdraftLimit = overdraftLimit;
    }

    @Override
    public void withdraw(double amount) {
        if (amount <= balance + overdraftLimit) {
            balance -= amount;
        } else {
            System.out.println("Withdrawal amount exceeds available funds and overdraft limit!");
        }
    }
}

// Investment Account subclass
class InvestmentAccount extends Account {
    private double interestRate;

    public InvestmentAccount(String accountNumber, double balance, double interestRate) {
        super(accountNumber, balance);
        this.interestRate = interestRate;
    }

    public void addInterest() {
        balance += balance * interestRate;
    }
}

// Main class to demonstrate usage
public class BankingSystem {
    public static void main(String[] args) {
        // Creating different types of accounts
        SavingsAccount savingsAccount = new SavingsAccount("SA001", 1000, 0.05);
        CheckingAccount checkingAccount = new CheckingAccount("CA001", 2000, 500);
        InvestmentAccount investmentAccount = new InvestmentAccount("IA001", 5000, 0.08);

        // Depositing and withdrawing from accounts
        savingsAccount.deposit(200);
        savingsAccount.addInterest();
        savingsAccount.displayBalance();

        checkingAccount.withdraw(2500);
        checkingAccount.displayBalance();

        investmentAccount.addInterest();
        investmentAccount.displayBalance();
    }
}

