// Base Character class
class Character {
    protected String name;
    protected int health;
    protected int attackPower;

    public Character(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }

    public void attack(Character target) {
        System.out.println(name + " attacks " + target.name + " for " + attackPower + " damage.");
        target.takeDamage(attackPower);
    }

    public void takeDamage(int damage) {
        health -= damage;
        System.out.println(name + " takes " + damage + " damage.");
        if (health <= 0) {
            System.out.println(name + " has been defeated!");
        }
    }

    public void displayStatus() {
        System.out.println("Name: " + name);
        System.out.println("Health: " + health);
    }
}

// Warrior subclass
class Warrior extends Character {
    private int defense;

    public Warrior(String name, int health, int attackPower, int defense) {
        super(name, health, attackPower);
        this.defense = defense;
    }

    @Override
    public void takeDamage(int damage) {
        int reducedDamage = damage - defense;
        if (reducedDamage < 0) {
            reducedDamage = 0;
        }
        super.takeDamage(reducedDamage);
    }
}

// Mage subclass
class Mage extends Character {
    private int mana;

    public Mage(String name, int health, int attackPower, int mana) {
        super(name, health, attackPower);
        this.mana = mana;
    }

    public void castSpell(Character target) {
        if (mana >= 10) {
            System.out.println(name + " casts a spell on " + target.name);
            target.takeDamage(attackPower * 2);
            mana -= 10;
        } else {
            System.out.println(name + " does not have enough mana to cast a spell.");
        }
    }
}

// Archer subclass
class Archer extends Character {
    private int arrows;

    public Archer(String name, int health, int attackPower, int arrows) {
        super(name, health, attackPower);
        this.arrows = arrows;
    }

    @Override
    public void attack(Character target) {
        if (arrows > 0) {
            super.attack(target);
            arrows--;
            System.out.println(name + " has " + arrows + " arrows left.");
        } else {
            System.out.println(name + " has no arrows left!");
        }
    }
}

// Main class to demonstrate usage
public class Game {
    public static void main(String[] args) {
        // Creating characters
        Warrior warrior = new Warrior("Thor", 150, 20, 5);
        Mage mage = new Mage("Merlin", 100, 15, 50);
        Archer archer = new Archer("Robin", 120, 10, 30);

        // Displaying initial statuses
        warrior.displayStatus();
        mage.displayStatus();
        archer.displayStatus();

        // Simulating a battle
        warrior.attack(mage);
        mage.castSpell(warrior);
        archer.attack(warrior);

        // Displaying statuses after battle
        warrior.displayStatus();
        mage.displayStatus();
        archer.displayStatus();
    }
}

