// Subclass BankAccount
class BankAccount extends Account {
    public BankAccount(int accountNumber, double balance) {
        super(accountNumber, balance);
    }

    // Implementing withdraw method
    @Override
    public void withdraw(double amount) {
        if (amount > 0 && amount <= getBalance()) {
            setBalance(getBalance() - amount);
            System.out.println("Withdrawn: $" + amount);
        } else {
            System.out.println("Invalid amount or insufficient balance.");
        }
    }

    // Implementing deposit method
    @Override
    public void deposit(double amount) {
        if (amount > 0) {
            setBalance(getBalance() + amount);
            System.out.println("Deposited: $" + amount);
        } else {
            System.out.println("Invalid amount.");
        }
    }
}

// Main class to demonstrate the BankAccount class
public class Main {
    public static void main(String[] args) {
        // Creating a BankAccount object
        BankAccount account = new BankAccount(123456, 1000.0);

        // Demonstrating encapsulation by accessing account number and balance using getter methods
        System.out.println("Account Number: " + account.getAccountNumber());
        System.out.println("Balance: $" + account.getBalance());

        // Depositing and withdrawing money
        account.deposit(500.0);
        account.withdraw(200.0);

        // Checking balance after transactions
        System.out.println("New Balance: $" + account.getBalance());
    }
}

