// Superclass Shape
public class Shape {
    // Method to calculate area
    public double calculateArea() {
        return 0;
    }

    // Method to calculate perimeter
    public double calculatePerimeter() {
        return 0;
    }
}

// Subclass Circle extending Shape
class Circle extends Shape {
    private Point center;
    private double radius;

    // Constructor for Circle
    public Circle(Point center, double radius) {
        this.center = center;
        this.radius = radius;
    }

    // Override method to calculate area for Circle
    @Override
    public double calculateArea() {
        return Math.PI * radius * radius;
    }

    // Override method to calculate perimeter for Circle
    @Override
    public double calculatePerimeter() {
        return 2 * Math.PI * radius;
    }
}

// Subclass Rectangle extending Shape
class Rectangle extends Shape {
    private Point topLeft;
    private Point bottomRight;

    // Constructor for Rectangle
    public Rectangle(Point topLeft, Point bottomRight) {
        this.topLeft = topLeft;
        this.bottomRight = bottomRight;
    }

    // Override method to calculate area for Rectangle
    @Override
    public double calculateArea() {
        double width = Math.abs(topLeft.getX() - bottomRight.getX());
        double height = Math.abs(topLeft.getY() - bottomRight.getY());
        return width * height;
    }

    // Override method to calculate perimeter for Rectangle
    @Override
    public double calculatePerimeter() {
        double width = Math.abs(topLeft.getX() - bottomRight.getX());
        double height = Math.abs(topLeft.getY() - bottomRight.getY());
        return 2 * (width + height);
    }
}

// Class Point representing a point
class Point {
    private double x;
    private double y;

    // Constructor for Point
    public Point(double x, double y) {
        this.x = x;
        this.y = y;
    }

    // Getter methods for x and y coordinates
    public double getX() {
        return x;
    }

    public double getY() {
        return y;
    }
}

// Main class for testing
public class Main {
    public static void main(String[] args) {
        // Creating points
        Point center = new Point(0, 0);
        Point topLeft = new Point(0, 5);
        Point bottomRight = new Point(10, 0);

        // Creating shapes
        Circle circle = new Circle(center, 5);
        Rectangle rectangle = new Rectangle(topLeft, bottomRight);

        // Calculating and printing area and perimeter of Circle
        System.out.println("Circle:");
        System.out.println("Area: " + circle.calculateArea());
        System.out.println("Perimeter: " + circle.calculatePerimeter());

        // Calculating and printing area and perimeter of Rectangle
        System.out.println("\nRectangle:");
        System.out.println("Area: " + rectangle.calculateArea());
        System.out.println("Perimeter: " + rectangle.calculatePerimeter());
    }
}

